  components: {
    WeatherCard //「WeatherCard」コンポーネントを使えるようにする
  },
  data() {
    return {
      city: '' //cityの値を空にしておく
    };
  },
  computed: {
    ...mapState(['weather', 'error']) //（WeatherCardコンポーネントに）「weather」プロパティと「error」プロパティを登録
